/* invoice.css */

/* Scope only invoice tables */
/* print_format.css */

/* Reset for clean PDF output */
body {
    font-family: "Helvetica", "Arial", sans-serif;
    font-size: 13px;
    color: #333;
    margin: 20px;
    line-height: 1.5;
}

/* Header */
.print-header {
    text-align: center;
    border-bottom: 2px solid #444;
    margin-bottom: 20px;
    padding-bottom: 10px;
}

.print-header h1 {
    margin: 0;
    font-size: 20px;
    font-weight: bold;
}

.print-header p {
    margin: 2px 0;
    font-size: 12px;
    color: #555;
}

/* Table styling */
.print-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 15px;
    font-size: 12px;
}

.print-table th,
.print-table td {
    border: 1px solid #ddd;
    padding: 8px 10px;
    text-align: left;
}

.print-table th {
    background-color: #f4f6f8;
    font-weight: 600;
    color: #222;
}

.print-table tr:nth-child(even) {
    background-color: #fafafa;
}

/* Footer */
.print-footer {
    margin-top: 30px;
    text-align: center;
    font-size: 11px;
    color: #666;
    border-top: 1px solid #ccc;
    padding-top: 8px;
}

/* Signature area */
.signature-block {
    margin-top: 40px;
    display: flex;
    justify-content: space-between;
    font-size: 12px;
}

.signature-block div {
    width: 45%;
    text-align: center;
}

.signature-line {
    margin-top: 50px;
    border-top: 1px solid #000;
    padding-top: 5px;
}

table.invoice-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 15px;
    font-size: 13px;
}

table.invoice-table th,
table.invoice-table td {
    border: 1px solid #ddd;
    padding: 8px 10px;
    text-align: left;
}

table.invoice-table th {
    background-color: #f4f6f8;
    font-weight: 600;
    color: #333;
}

table.invoice-table tr:nth-child(even) {
    background-color: #fafafa;
}

table.invoice-table tr:hover {
    background-color: #f1f1f1;
}
